import React, { useEffect, useState } from "react";
import axios from "axios";

function Fib() {
  const [state, setState] = useState({
    seenIndexes: [],
    values: {},
    index: "",
  });

  useEffect(() => {
    fetchValues();
    fetchIndexes();
  }, []);

  async function fetchValues() {
    const values = await axios.get("/api/values/current");
    setState({ values: values.data });
  }

  async function fetchIndexes() {
    const seenIndexes = await axios.get("/api/values/all");
    setState({ seenIndexes: seenIndexes.data });
  }

  function renderSeenIndexes() {
    console.log(state.seenIndexes);
    if (state.seenIndexes) {
      return state.seenIndexes.map(({ number }) => number).join(", ");
    }
  }

  function renderValues() {
    const entries = [];

    for (let key in state.values) {
      entries.push(
        <div key={key}>
          For index {key} i Calculated {state.values[key]}
        </div>
      );
      return entries;
    }
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    await axios.post("/api/values", {
      index: state.index,
    });
    setState({ index: "" });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>Enter your index: </label>
        <input
          value={state.index}
          onChange={(event) => setState({ index: event.target.value })}
        />
        <button>Submit</button>
      </form>

      <h3>Indexes i have seen: </h3>
      {renderSeenIndexes()}

      <h3>Calculated values: </h3>
      {renderValues()}
    </div>
  );
}

export default Fib;
